def fun1():#二进制字符串转换字符串
    #需要转换的字符串
    f = ''
    b = ''
    i = 0
    j = 7
    while j <= len(f):
        a = '0' + f[i:j]
        b += chr(int(a,2))
        i = j
        j += 7
    print(b)
def fun2():#字符串转换二进制字符串
    #需要转换的字符串
    f = ' '
    b = ''
    c = ''
    for i in f:
        a = str(bin(ord(i)))
        b = a[2:].zfill(7)
        c += b
    print(c)
fun1()
